#include <bits/stdc++.h>using namespace std; #define ll long long#define gc getchar_unlocked#define fo(i, n) for (i = 0; i < n; i++)#define Fo(i, k, n) for (i = k; k < n ? i < n : i > n; k < n ? i += 1 : i -= 1)#define si(x) scanf("%d", &x)#define sl(x) scanf("%lld", &x)#define ss(s) scanf("%s", s)#define pi(x) printf("%d\n", x)#define pl(x) printf("%lld\n", x)#define ps(s) printf("%s\n", s)#define deb(x) cout << #x << "=" << x << endl#define deb2(x, y) cout << #x << "=" << x << "," << #y << "=" << y << endl#define pb push_back#define mp make_pair#define F first#define S second#define all(x) x.begin(), x.end()#define clr(x) memset(x, 0, sizeof(x))#define sortall(x) sort(all(x))#define tr(it, a) for (auto it = a.begin(); it != a.end(); it++)#define PI 3.1415926535897932384626#define MOD 1000000007typedef pair<int, int> pii;typedef pair<ll, ll> pl;typedef vector<bool> vb;typedef vector<int> vi;typedef vector<ll> vl;typedef vector<pii> vpii;typedef vector<pl> vpl;typedef vector<vi> vvi;typedef vector<vl> vvl; void bubbleSort(vector<ll>& arr,vector<int>& ans) {    int n = arr.size();    for (int i = 0; i < n - 1; i++) {        bool swapped = false;         for (int j = 0; j < n - i - 1; j++) {            if (arr[j] > arr[j + 1]) {                swap(arr[j], arr[j + 1]);                ans.push_back(j+1);                swapped = true;            }        }        if (!swapped) break;     }} void solve() {    ll n; cin>>n;    vector<ll> a(n),b(n);    for(int i=0; i<n ;i++){        cin>>a[i];    }    for(int i=0; i<n ;i++){        cin>>b[i];    }     ll ans=0;     vector<int> opsA,opsB;    bubbleSort(a,opsA);    bubbleSort(b,opsB);    ans+=opsA.size();    ans+=opsB.size();     vector<int> op;     for(int i=0; i<n ;i++){        if(a[i]>b[i]){            op.push_back(i+1);            ans++;        }    }     cout<<ans<<endl;    for(auto i:opsA){        cout<<1<<" "<<i<<endl;    }    for(auto i:opsB){        cout<<2<<" "<<i<<endl;    }    for(auto i:op){        cout<<3<<" "<<i<<endl;    } } int main() {    ios_base::sync_with_stdio(0);    cin.tie(0); cout.tie(0);    int tc = 1;    cin >> tc;    for (int t = 1; t <= tc; t++) {        // cout << "Case #" << t << ": ";        solve();    }    return 0;}